//Functions
(function(window,undefined ) {
	var sitescripts = {
		ui:{
			//Script to ensure Snowcone will work for legacy browsers, that don't support the :before pseudo class
			addicons:function(){
				//Create object with list of icons
					var icons = {
					    ico_alert: "A",
					    ico_cross: "B",
					    ico_tick: "C",
						ico_expand: "D",
						ico_contract: "E",
						ico_view: "F",
						ico_sync: "G",
						ico_home: "H",
						ico_outline_down: "I",
						ico_outline_up: "J",
						ico_outline_right: "K",
						ico_outline_left: "L",
						ico_stop: "M",
						ico_mapmarker: "N",
						ico_favourite: "O",
						ico_profile: "P",
						ico_magnify: "Q",
						ico_comment: "R",
						ico_settings: "S",
						ico_edit: "T",
						ico_email: "U",
						ico_shop_bag: "V",
						ico_logout: "W", 
						ico_heart: "X", 
						ico_rss: "Z", 
						ico_link: "a", 
						ico_fill_down: "b", 
						ico_fill_up: "c", 
						ico_fill_right: "d", 
						ico_fill_left: "e", 
						ico_facebook: "f", 
						ico_twitter: "g", 
						ico_googleplus: "h", 
						ico_blacklist: "i", 
						ico_twitter_bird: "t", 
						ico_twitter_plain: "u", 
						ico_tag: "t", 
						ico_speedo: "y",  
						ico_tangentsnowball: "z"
					};
				//Detect IE version	
					var browserver = parseInt($.browser.version, 10)
				
				//Run icon insertion	
					if ($.browser.msie && browserver == 6 || browserver == 7){
						$(".app-ico").each(function(){
							var icon = $(this).attr("class").split(/\s/);
							for(key in icons){
								if([key]==icon[1]){
									$(this).prepend("<span class='app-legacy_ico'>" + icons[key] + "</span>");
								};
							}
						});
					}
			},
			skipheader: function(){
				var header_height = $("#gl-header_pri").height();
				$(window).stop().scrollTo(header_height+1,800);
			},
			scrolltop: function(){
				var canvas_height = $(window).height();
				$("#gl-toplink").removeAttr("href");
				$("#gl-toplink").click(function(){
					$(window).stop().scrollTo(0,canvas_height);
				});	
			},
			navhover: function(){
				var site_title = $("#gl-header_pri h1").text();
				$("#gl-nav_pri li a").each(function(){
					var nav_title = $(this).text();
					$(this).hover(
						function(){
							$("#gl-header_pri h1").text(nav_title);
						},
						function(){
							$("#gl-header_pri h1").text(site_title);
						}
					);
				});
			}	
		},
		modules:{
			disqus: function(){
			    var disqus_shortname = 'jackarmley'; 
			    (function() {
			        var dsq = document.createElement('script'); dsq.type = 'text/javascript'; dsq.async = true;
			        dsq.src = 'http://' + disqus_shortname + '.disqus.com/embed.js';
			        (document.getElementsByTagName('head')[0] || document.getElementsByTagName('body')[0]).appendChild(dsq);
			    })();
			},
			tweetbox: function(){
				var tweetbox_width = ""
				tweetbox_width = $("#gl-tweetbox").parent().width();
				tweetbox_height = tweetbox_width/3;
				
				function setbox(){
	      			twttr.anywhere(function (T) {
	        			T("#gl-tweetbox").tweetBox({
	        				label:"",
	        				defaultContent: "An article by @jackarmley",
	        				width:tweetbox_width,
	        				height:tweetbox_height
	        			});
					});
				}
				function removebox(){
					$("#gl-tweetbox").empty();
				}
				setbox()

				$(window).resize(function(){
					tweetbox_width = $("#gl-tweetbox").parent().width();
					removebox()
					setbox()
				});
				
			}	
		},
		pagespecific:{
			contactpage: function(){
				var messageplaceholder=$("#message").text();
				$("#message").focus(
					function(){
						if(messageplaceholder == "Go ahead, say something"){
							$(this).text("");
						}
				});
				$("#message").blur(
					function(){
						if(messageplaceholder == "Go ahead, say something"){
							$(this).text(messageplaceholder);
						}
				});
			},
			workpage: function(){
				$(".state-slider").flexslider({
					directionNav:false,
					animation: "slide"
				});
				var stateslider_navwidth = $(".flex-control-nav").width();
				$(".flex-control-nav").css("width",stateslider_navwidth).addClass("state-nofloat");
			}
		},
		mobile:{
			hideurlbar: function(){
				addEventListener("load", function() { setTimeout(hideURLbar, 0); }, false);
				function hideURLbar(){
					window.scrollTo(0,1);
				}
			}
		}
	} 
window.sitescripts = sitescripts;
})(window);	  
	

$(document).ready(function() {
	//Establish devices
		var mobile = 0;
		var canvas_width = $(window).width();
		if(canvas_width<=600){
			mobile = 1;
		}
	
	//Run mobile only scripts
		if(mobile==1){
			sitescripts.mobile.hideurlbar()
		}		
		
	//Run globals
		if(mobile==0){
			sitescripts.ui.addicons()
			sitescripts.ui.navhover()
			$(window)._scrollable();
		}
			
	//section specific
	
		var section=$("html").attr("id");
		var subpage_classstack=$("html").attr("class").split(/\s/);
		var subpage = subpage_classstack[0];
		
		if(section=="pg-contact"){
			sitescripts.pagespecific.contactpage()
		}
		if(section=="pg-work"){
			sitescripts.pagespecific.workpage()	
		}
		
	//subsection specific	
		
		if(subpage=="p-article_post"){
			//sitescripts.modules.disqus()
			sitescripts.modules.tweetbox()
			
			if(mobile==0){
				sitescripts.ui.scrolltop()
				sitescripts.ui.skipheader()
			}
		}
		
		if(subpage=="p-work_detail"){
			
			if(mobile==0){
				sitescripts.ui.skipheader()
			}
		}			 
});
